mapper.update:
update trade

        state = #{state},

        notes = #{notes}


where trade_id = #{tradeID}

mapper.select:
select count(*) from trade


        and state = #{state}


order by trade_id desc
limit #{offset}, #{pageSize}

select * from trade


        and state = #{state}


order by trade_id desc
limit #{offset}, #{pageSize}

select count(*) from trade

    buyer_id = #{userID}

        and state = #{state}


order by trade_id desc
limit #{offset}, #{pageSize}

select * from trade

        buyer_id = #{userID}

        and state = #{state}


order by trade_id desc
limit #{offset}, #{pageSize}

select count(*) from trade

    seller_id = #{userID}

        and state = #{state}


order by trade_id desc
limit #{offset}, #{pageSize}

select * from trade

    seller_id = #{userID}

        and state = #{state}


order by trade_id desc
limit #{offset}, #{pageSize}
