mapper.update:
UPDATE trade

        state = #{state},

        notes = #{notes}


WHERE trade_id = #{tradeID}

mapper.select:
SELECT COUNT(*) FROM trade


        AND state = #{state}


ORDER BY trade_id DESC
LIMIT #{offset}, #{pageSize}

