mapper.update:
UPDATE trade

        state = #{state},

        notes = #{notes}


WHERE trade_id = #{tradeID}

mapper.select:
SELECT COUNT(*) FROM trade


        AND state = #{state}


ORDER BY trade_id DESC
LIMIT #{offset}, #{pageSize}

SELECT trade_id, buyer_name, seller_name, trade.goods_id, state, trade.notes, goods_name, picture
FROM trade, goods

        trade.goods_id = goods.goods_id

        AND state = #{state}


ORDER BY trade_id DESC
LIMIT #{offset}, #{pageSize}

SELECT COUNT(*) FROM trade

    buyer_name = #{userName}

        AND state = #{state}


ORDER BY trade_id DESC
LIMIT #{offset}, #{pageSize}

