mapper.update:
UPDATE trade

        state = #{state},

        notes = #{notes}


WHERE trade_id = #{tradeID}

mapper.select:
SELECT COUNT(*) FROM trade


        AND state = #{state}


ORDER BY trade_id DESC
LIMIT #{offset}, #{pageSize}

SELECT trade_id, buyer_id, u1.nick_name as buyerNickName, u1.avatar as buyerAvatar, seller_id, u2.nick_name as sellerNickName, u2.avatar as sellerAvatar, trade.goods_id, goods_name, picture, state, trade.notes FROM trade, goods, user u1, user u2
trade.goods_id = goods.goods_id AND trade.buyer_id = u1.user_id AND trade.seller_id = u2.user_id
AND state = #{state}
ORDER BY trade_id DESC LIMIT #{offset}, #{pageSize}

SELECT COUNT(*) FROM trade

    buyer_id = #{userID}

        AND state = #{state}


ORDER BY trade_id DESC
LIMIT #{offset}, #{pageSize}

SELECT trade_id, seller_id, user.nick_name as sellerNickName, user.avatar as sellerAvatar,
       trade.goods_id, goods_name, picture, state, trade.notes
FROM trade, goods, user

        trade.goods_id = goods.goods_id
        AND trade.seller_id = user.user_id
        AND trade.buyer_id = #{userID}

        AND state = #{state}


ORDER BY trade_id DESC
LIMIT #{offset}, #{pageSize}

SELECT COUNT(*) FROM trade

    seller_id = #{userID}

        AND state = #{state}


ORDER BY trade_id DESC
LIMIT #{offset}, #{pageSize}

